sourceCode:
/* ==UserStyle==
@name         Wikipedia Dark
@namespace    StylishThemes
@version      3.7.0
@description  Wikipedia Dark theme
@author       StylishThemes
@homepageURL  https://github.com/StylishThemes/Wikipedia-Dark
@supportURL   https://github.com/StylishThemes/Wikipedia-Dark/issues/new/choose
@updateURL    https://raw.githubusercontent.com/StylishThemes/Wikipedia-Dark/master/wikipedia-dark.user.css
@license      CC-BY-SA-4.0
@advanced color     base-color "Base color"    #4183C4
@advanced color      tab-color "Tab color"     #e08b26
@advanced color  visited-color "Visited color" #295887
@advanced dropdown bg-selected "Background image" {
  crosshatch "Cross-Hatch" <<<EOT
  url('data:image/png;base64,') EOT;
  uniform "Uniform Tile" <<<EOT
  url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAGQAAABkAQMAAABKLAcXAAAABlBMVEUfHx8aGhrh02scAAAAH0lEQVR4AWMQRAICeHn/kcAH8vSN6hvVN6pvVN+oPgAPQ4LItqOMdQAAAABJRU5ErkJggg==') EOT;
  random "Random Tile" <<<EOT
  url('data:image/png;base64,') EOT;
  custom "Custom" <<<EOT
  var(--bg-custom) EOT;
  none "None" <<<EOT
  none EOT;
}
@advanced text bg-custom "Custom background; include url()" "url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAPCAMAAAAMCGV4AAAAD1BMVEUREREREREAAAAREREWFhkN5cwCAAAABHRSTlNfGAAwFBG7CgAAACxJREFUeAFjYGJiZGBhhhMMYIIRTjCgcIE8VC4TKpeRmYEZiTtIzGNihhNMAJJAATdKr5xFAAAAAElFTkSuQmCC')"
@advanced dropdown bg-options "Background image type" {
  Tiled "Tiled" <<<EOT
  background-repeat: repeat !important;
  background-size: auto !important;
  background-position: left top !important; EOT;
  Fit "Fit Window" <<<EOT
  background-repeat: no-repeat !important;
  background-size: cover !important;
  background-position: center top !important; EOT;
}
@advanced dropdown bg-attachment "Background image attachment" {
  Scroll "Scroll" <<<EOT
  scroll EOT;
  Fixed "Fixed" <<<EOT
  fixed EOT;
}
@preprocessor uso
==/UserStyle== */
@-moz-document regexp("https?:\/\/.*wik(i|t).*(org|jp).*") {
  /* Global color palette */
  :root {
    --base-color: /*[[base-color]]*/;
    --base-color-rgb: /*[[base-color-rgb]]*/;
    --base-color-rgba-3: rgba(var(--base-color-rgb), 3);
    --base-color-rgba-8: rgba(var(--base-color-rgb), 8);
    --tab-color: /*[[tab-color]]*/;
    --visited-color: /*[[visited-color]]*/;
    --bg-custom: /*[[bg-custom]]*/;
    --bg-selected: /*[[bg-selected]]*/;
    --bg-attachment: /*[[bg-attachment]]*/;
    /* base palette */
    --black: #000;
    --gray-1: #111;
    --gray-2: #222;
    --gray-3: #333;
    --gray-4: #444;
    --gray-5: #555;
    --gray-7: #777;
    --gray-a: #aaa;
    --gray-c: #ccc;
    --gray-d: #ddd;
    --gray-e: #eee;
    --white: #fff;
    --gray-18: #181818;
    --gray-28: #282828;
    --gray-2f: #2f4f4f;
    --gray-69: #696969;
    --gray-9a: #9a9a9a;
    --gray-b9: #b9b9b9;
    --gray-bc: #bcbcbc;
    --blue: #00f;
    --blue-1: #36c;
    --blue-2: #182030;
    --blue-3: #212c43;
    --blue-4: #3c5487;
    --red: #f00;
    --red-0: #400;
    --red-1: #c22;
    --red-2: #d33;
    --red-3: #722;
    --red-rgb: rgba(119, 34, 34, .4);
    --red-4: #ff4242;
    --green: #0f0;
    --green-1: #390;
    --green-2: #165316;
    --green-3: #061;
    --green-4: #002800;
    --brown-1: #321;
    --brown-2: #542;
    --brown-3: #261d08;
    --brown-4: #4b380d;
    --yellow-1: #b28200;
    --yellow-2: #ffdd7f;
    --orange-1: #e53e00;
    --orange-2: #e08b26;
    --orange-3: #4d2b01;
    --purple-d: #241738;
    --purple-0: #312641;
    --purple-1: #382b4a;
    --purple-2: #768;
    --pink-0: #3e243c;
    --pink-1: #6b3e68;
    --pink-2: #965691;
    --pink-3: #ffbefa;
    --cyan-1: #194d41;
    --ores-1: #222800;
    --ores-2: #222000;
    --ores-3: #221800;
    --black_17: rgba(0, 0, 0, .175);
    --black_2: rgba(0, 0, 0, .2);
    --black_25: rgba(0, 0, 0, .25);
    --black_5: rgba(0, 0, 0, .5);
    --black_8: rgba(0, 0, 0, .8);
    --gray-2_75: rgba(34, 34, 34, .75);
    --gray-3_5: rgba(51, 51, 51, .5);
    --gray-5_25: rgba(85, 85, 85, .25);
    --gray-6_5: rgba(100, 100, 100, .5);
    --white_1: rgba(255, 255, 255, .1);
    --white_25: rgba(255, 255, 255, .25);
    --diff-del: #c22;
    --diff-add: #36c;
    --revs-old: #b28200;
    --revs-new: #070;
    /* assessment colors */
    --assess-1: #06c;
    --assess-2: #29c;
    --assess-3: #070;
    --assess-4: #6b0;
    --assess-5: #993;
    --assess-6: #b60;
    --assess-7: #921; /* was #901 */
    --assess-8: #85f;
    --assess-9: #070;
    --assess-10: #0b6;
    --assess-11: #a4c;
    --assess-12: #568;
    /* meta wiki */
    --green-a0: #2f4500;
    --green-a1: #27560f;
    --green-a2: #016101;
    --green-a3: #305229;
    --green-a4: #50612b;
    --green-a5: #676702;
    --bluei-a0: #014523;
    --bluei-a1: #004e4e;
    --bluei-a2: #05493b;
    --bluei-a3: #1b3a2a;
    /* WD query service */
    --cm-0: #141414;
    --cm-1: #27282e;
    --cm-2: #f9ee98;
    --cm-3: #fc0;
    --cm-4: #ca7841;
    --cm-5: #8da6ce;
    --cm-6: #607392;
    --cm-7: #cda869;
    --cm-8: #8f9d6a;
    --cm-9: #bd6b18;
    --cm-10: #f7f7f7;
    --cm-11: #997643;
    --cm-12: #d6bb6d;
    --cm-13: #ff6400;
    --cm-14: #aeaeae;
    --cm-15: #ad9361;
  }
  .cm-comment,
  .cm-link {
    font-style: italic;
  }
  .CodeMirror {
    background: var(--gray-18);
  }
  .CodeMirror-gutters {
    background: var(--gray-2);
    border-right: 1px solid var(--gray-4);
  }
  .CodeMirror-guttermarker {
    color: var(--white);
  }
  .CodeMirror-guttermarker-subtle,
  .CodeMirror-linenumber {
    color: var(--gray-a);
  }
  .CodeMirror-cursor {
    border-left: 2px solid var(--white);
  }
  .CodeMirror-activeline-background {
    background: var(--cm-1);
  }
  .CodeMirror-matchingbracket {
    outline: 1px solid var(--gray);
    color: var(--white);
  }
  .CodeMirror-line::selection,
  .CodeMirror-line > span::selection,
  .CodeMirror-line > span > span::selection {
    background: var(--cm-1);
  }
  .cm-keyword {
    color: var(--cm-2);
  }
  .cm-atom {
    color: var(--cm-3);
  }
  .cm-number {
    color: var(--cm-4);
  }
  .cm-def,
  .cm-mw-section-header,
  .cm-mw-link-pagename,
  .cm-mw-link-bracket,
  .cm-mw-link-delimiter,
  .cm-mw-indenting,
  .cm-mw-extlink-bracket,
  .cm-mw-list,
  .cm-mw-apostrophes-bold,
  .cm-mw-apostrophes-italic,
  .cm-mw-link-tosection {
    color: var(--cm-5);
  }
  .cm-mw-skipformatting {
    background-color: var(--cm-6);
  }
  span.cm-def,
  span.cm-tag,
  .cm-variable-2,
  .cm-variable-3,
  .cm-mw-extlink-protocol,
  .cm-mw-free-extlink-protocol,
  .cm-mw-extlink,
  .cm-mw-free-extlink {
    color: var(--cm-6);
  }
  .cm-builtin,
  .cm-operator {
    color: var(--cm-7);
  }
  .cm-comment {
    color: var(--gray-7);
    font-weight: 400;
  }
  .cm-string,
  .cm-mw-htmltag-name,
  .cm-mw-htmltag-bracket,
  .cm-mw-exttag-name,
  .cm-mw-exttag-bracket,
  .cm-mw-htmltag-attribute,
  .cm-mw-exttag-attribute,
  .cm-mw-mnemonic {
    color: var(--cm-8);
  }
  .cm-string-2 {
    color: var(--cm-9);
  }
  .cm-meta {
    background-color: var(--gray-18);
    color: var(--cm-10);
  }
  .cm-tag {
    color: var(--cm-11);
  }
  .cm-attribute {
    color: var(--cm-12);
  }
  .cm-header,
  .cm-mw-table-bracket,
  .cm-mw-table-definition,
  .cm-mw-table-delimiter {
    color: var(--cm-13);
  }
  .cm-hr {
    color: var(--cm-14);
  }
  .cm-link {
    color: var(--cm-15);
    text-decoration: none;
  }
  .cm-error {
    border-bottom: 1px solid var(--red);
  }
  .cm-mw-template-name,
  .cm-mw-template-bracket,
  .cm-mw-template,
  .cm-mw-template-argument-name,
  .cm-mw-template-delimiter {
    color: var(--cm-10);
  }
  /*# sourceURL=ace/css/ace-tm */
  .ace-tm .ace_gutter {
    background: var(--cm-1);
    color: var(--cm-10);
  }
  .ace-tm .ace_print-margin {
    background: var(--cm-1);
  }
  .ace-tm .ace_fold {
    background-color: var(--cm-6);
  }
  .ace-tm {
    background-color: var(--cm-0);
    color: var(--cm-10);
  }
  .ace-tm .ace_cursor {
    color: var(--cm-10);
  }
  .ace-tm .ace_invisible {
    color: var(--cm-10);
  }
  .ace-tm .ace_storage,
  .ace-tm .ace_keyword {
    color: var(--cm-5);
  }
  .ace-tm .ace_constant {
    color: var(--cm-13);
  }
  .ace-tm .ace_constant.ace_buildin {
    color: var(--cm-14);
  }
  .ace-tm .ace_constant.ace_language {
    color: var(--cm-14);
  }
  .ace-tm .ace_constant.ace_library {
    color: var(--cm-8);
  }
  .ace-tm .ace_invalid {
    background-color: rgba(255, 0, 0, .1);
    color: var(--cm-13);
  }
  .ace-tm .ace_support.ace_function {
    color: var(--cm-5);
  }
  .ace-tm .ace_support.ace_constant {
    color: var(--cm-8);
  }
  .ace-tm .ace_support.ace_type,
  .ace-tm .ace_support.ace_class {
    color: var(--cm-14);
  }
  .ace-tm .ace_keyword.ace_operator {
    color: var(--cm-14);
  }
  .ace-tm .ace_string {
    color: var(--cm-8);
  }
  .ace-tm .ace_comment {
    color: var(--cm-14);
  }
  .ace-tm .ace_comment.ace_doc {
    color: var(--cm-5);
  }
  .ace-tm .ace_comment.ace_doc.ace_tag {
    color: var(--cm-14);
  }
  .ace-tm .ace_constant.ace_numeric {
    color: var(--cm-5);
  }
  .ace-tm .ace_variable {
    color: var(--cm-12);
  }
  .ace-tm .ace_xml-pe {
    color: var(--cm-11);
  }
  .ace-tm .ace_entity.ace_name.ace_function {
    color: var(--cm-5);
  }
  .ace-tm .ace_heading {
    color: var(--cm-5);
  }
  .ace-tm .ace_list {
    color: var(--cm-13);
  }
  .ace-tm .ace_meta.ace_tag {
    color: var(--cm-6);
  }
  .ace-tm .ace_string.ace_regex {
    color: var(--cm-13);
  }
  .ace-tm .ace_marker-layer .ace_selection {
    background: var(--cm-1);
  }
  .ace-tm.ace_multiselect .ace_selection.ace_start {
    box-shadow: 0 0 3px 0 var(--cm-10);
  }
  .ace-tm .ace_marker-layer .ace_step {
    background: var(--cm-3);
  }
  .ace-tm .ace_marker-layer .ace_stack {
    background: var(--cm-2);
  }
  .ace-tm .ace_marker-layer .ace_bracket {
    border: 1px solid var(--cm-14);
  }
  .ace-tm .ace_marker-layer .ace_active-line {
    background: rgba(0, 0, 0, .07);
  }
  .ace-tm .ace_gutter-active-line {
    background-color: var(--gray-3);
  }
  .ace-tm .ace_marker-layer .ace_selected-word {
    background: var(--gray-3);
    border: 1px solid var(--cm-11);
  }
  .ace-tm .ace_indent-guide {
    background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAACCAYAAACZgbYnAAAAE0lEQVQImWP4////f4bLly//BwAmVgd1/w11/gAAAABJRU5ErkJggg==") right repeat-y;
  }
  .ve-ui-mwAceEditorWidget .ace_editor {
    border-color: var(--cm-6);
  }
}
@-moz-document regexp("https?:\/\/(([\w\-]{2,}\.)?(wiki(pedia|books|news|quote|source|versity|voyage)|wiktionary)|(www|test)\.(mediawiki|wiki(data|dot))|(meta|wikimania|commons|otrs-wiki|species|wikitech|incubator|outreach|foundation|donate)\.wikimedia)\.org\/.*"),
               regexp("https?:\/\/wiki\.(archlinux|mozilla)\.(org|jp)\/.*$"),
               regexp("https?:\/\/wikiless\.org\/.*$") {
  /* transparent background */
  html,
  body,
  .mw-body[role="main"],
  .skin-vector-max-width .mw-page-container,
  .skin-vector #mw-panel,
  .mw-page-container {
    background-color: var(--gray-2);
    background-image: var(--bg-selected);
    background-clip: border-box;
    background-origin: padding-box;
    background-attachment: var(--bg-attachment);
    /*[[bg-options]]*/
    color: var(--gray-c);
  }
  div.vectorTabs ul li,
  tt[style="background: white;"],
  span:not(.legend-color):not([class*="wikEd"]):not([style*="background"]):not(.wikibase-toolbar-wrapper):not(.tool),
  tr[style*="background:#F5FFFA" i],
  th[style*="background-color: #fff" i],
  blockquote[style*="background-color: white" i],
  table.flextable,
  table.flextable .invisible,
  table#noarticletext,
  table#noarticletext table,
  :not(.color_swatch) > div[style*="background"]:not([style*="#90EE90"]),
  .navbox-list,
  .mw-wsmfinal-content,
  .mw-body-content,
  .referencetooltip > li + li,
  .mw-echo-notifications-badge,
  .ext-cards-card-list .ext-cards-card,
  .infobox span.plainlinks *,
  .tracklist,
  .vertical-navbox th a,
  .ns-0 ul,
  .ns-0 ol li,
  .metadata.plainlinks.stub,
  .mw-body-content #mw-content-text h2:not([id^="mp-"]),
  .mw-body-content .portal ul,
  .wikitable > tr,
  .wikitable > tr > th,
  .wikitable > tr > td,
  .wikitable > * > tr,
  .wikitable > * > tr > th,
  .wikitable > * > tr > td:not(.break),
  .wikitable th span[style*="background"],
  .inline-quote-talk,
  .oo-ui-fieldsetLayout-group .oo-ui-widget,
  .mw-parser-output .mod-gallery .whitebg img,
  .mw-parser-output .mod-gallery .gallerybox div,
  #mp-topbanner {
    background-color: transparent !important;
  }
  .color_swatch div[style*="background"] {
    color: var(--white_25) !important;
  }
  .mw-body,
  .parsoid-body {
    background-color: transparent !important;
    color: var(--gray-9a);
  }
  .mw-mmv-download-pane .mw-mmv-download-attribution {
    background-color: var(--gray-2);
    color: var(--gray-a);
  }
  .mw-mmv-download-pane .mw-mmv-download-attribution:hover {
    background-color: var(--gray-28);
    color: var(--gray-c);
  }
  .mw-mmv-permission-box,
  .mw-mmv-info-box {
    border-color: var(--gray-5);
    background-color: transparent !important;
  }
  div.vectorTabs li.selected,
  .mw-mmv-image-metadata,
  .navbox,
  .navbox-subgroup,
  .mwe-pt-article-row-odd,
  .mwe-pt-article-row-even,
  .ve-ui-mwSaveDialog-options,
  .mw-parser-output #siteContainer div:first-child,
  #mw-page-base {
    background-color: transparent;
  }
  .lang-list-button {
    border-color: var(--gray-5);
    outline-color: transparent !important;
  }
  .lang-list-border {
    background-color: var(--gray-5);
  }
  .lang-list-button:focus {
    box-shadow: inset 0 0 0 1px var(--base-color);
  }
  .lang-list-active .lang-list-button {
    outline: 1px solid var(--base-color);
    border-color: var(--base-color) !important;
  }
  /*** Overall ***/
  td,
  li,
  th:not([style*="background"]):not([dir]),
  caption,
  p code,
  label code,
  dl code,
  select#searchLanguage option,
  dt,
  .oo-ui-widget:not([placeholder]) div,
  .oo-ui-widget label,
  .oo-ui-textInputWidget .oo-ui-inputWidget-input,
  .oo-ui-messageDialog-title,
  .oo-ui-messageDialog-message,
  .mw-echo-ui-menuItemWidget > .oo-ui-labelElement-label,
  .mw-mmv-image-desc,
  .oo-ui-tabSelectWidget-framed .oo-ui-tabOptionWidget.oo-ui-optionWidget-selected,
  .guider_content h1,
  .guider_content p,
  .tfa-recent,
  .central-featured-lang .link-box small,
  .other-project-tagline,
  .mw-number-text,
  .tux-editor-header,
  .tux-breadcrumb,
  .mw-mmv-title,
  .ext-translate-language-selector-label,
  .mw-parser-output > div > blockquote > p,
  .mw-parser-output,
  .mw-echo-ui-menuItemWidget > .oo-ui-buttonElement-button > .oo-ui-labelElement-label,
  #pagehistory li.selected,
  #mwe-popups-settings main form label,
  #pt-anonuserpage,
  #mw-mwoauth-authorize-form,
  #contentSub,
  #contentSub2 {
    color: var(--gray-c);
  }
  .oo-ui-listToolGroup.oo-ui-widget-disabled,
  .oo-ui-listToolGroup-tools .oo-ui-tool.oo-ui-widget-disabled .oo-ui-tool-title,
  .oo-ui-popupToolGroup-tools .oo-ui-tool-link .oo-ui-tool-accel {
    color: var(--gray-7);
  }
  /* text color fix to black for https://en.wikipedia.org/wiki/Web_colors#Hex_triplet,
	   if this breaks something we need to differenciate specificity */
  .color-chart-x11-table tr[style*="color: black"] > td {
    color: var(--gray-1);
  }
  .wikitable[style*="color:white"] tr[style="color: black;"] td {
    color: var(--gray-1);
  }
  h1,
  h2,
  h3,
  h4,
  h5,
  h6,
  span[style*="color:#555555"],
  td[style^="background: #FFFFFF" i],
  td[style^="background:#FFFFFF" i],
  td[style^="background: #F5F" i],
  td[style^="background:#F5F" i],
  div[style*="color"]:not([style*="black;"]):not([style*="back"]):not([style*="966"]):not([style*="red"]),
  div.flaggedrevs_preview,
  div.plainlinks,
  input[type="number"],
  select,
  input,
  input[type="search"],
  input[type="submit"],
  span[style*="white-space:nowrap; line-height:120%; font-size:155%; color:#333;"],
  tr[style^="color:black"],
  span[title="To be announced"],
  div[style*="color: black;"],
  td[style*="color: #000000;"]:not([style^="background"]),
  td[style*="color:#000"]:not([style*="background"]),
  div[style*="color:black"],
  td[style*="color:darkslategray;"],
  a[href*="Wikipedia:Wikipedia_Signpost"] span,
  .infobox th[style*="background"]:not([style*="#eaecf0"]):not([style*="background-color:#b0c4de;"]):not([style*="background-color: #f9f"]):not([style*="background-color:#b0c4de"]):not([style*="background-color:#FFD"]):not([style*="background-color: #91FAFA"]):not([style="background-color: #b0c4de"]):not([style*="background-color: antiquewhite"]),
  .oo-ui-buttonElement-button,
  .oo-ui-buttonElement-button:hover,
  .suggestions-result,
  .central-featured-lang .link-box em,
  .table-partial[style*="background: #FFFFBB; color:black;"],
  .vertical-navbox th span,
  .mw-mmv-post-image,
  .mw-mmv-permission-box .mw-mmv-permission-text,
  #footer li {
    color: var(--gray-c) !important;
  }
  a,
  a span[style="color:darkblue;"],
  a:hover span[style="color:#002bb8"],
  a.wbc-editpage:hover,
  body .ui-button,
  span[style="color: #002BB8;"],
  span[style="color:#002bb8"],
  .CategoryTreeToggle,
  .toctogglelabel,
  .other-project-title,
  .oo-ui-buttonElement-frameless.oo-ui-widget-enabled.oo-ui-flaggedElement-progressive > .oo-ui-buttonElement-button,
  .ui-dialog a,
  .langlist a:hover,
  #firstHeading div.fastcci-buttonset > .ui-button,
  #toc a,
  #toc a span,
  #content a:not(.new):not([role="button"]):not(.mw-charinsert-item):not([title="Help:Template"]),
  #mw-navigation li:not(.new) a:not(.new),
  #mw-panel li:not(.new) a:not(.new),
