@-moz-document regexp("https?:\/\/([\w\-]{2,}\.m\.(wiki(pedia|books|news|quote|source|versity|voyage)|wiktionary)|m\.(mediawiki|wiki(data|dot))|(meta|commons|otrs-wiki|species|wikitech)\.m\.wikimedia)\.org\/.+") {
  body {
    background-color: var(--gray-2);
    background-image: var(--bg-selected);
    background-clip: border-box;
    background-origin: padding-box;
    background-attachment: var(--bg-attachment);
    /*[[bg-options]]*/
    color: var(--gray-c);
